
(function($){$.fn.ZEITPageWidth=function(options){var options=$.extend({switchwidth:1150},options);var switchStyle=function(styleName){$('link[rel*=style][title]').each(function(i){this.disabled=true;if(this.getAttribute('title')==styleName){this.disabled=false;$.cookie('zeitstyle',styleName,{expires:365,path:'/',domain:'zeit.de'});}});if(isSafariOnTiger()){$('#bottom').hide().show();}
if(document.getElementById('wrapper').style.maxHeight==undefined){var changeme="";if($('#informatives .tools').size()>0){changeme=$('ul.tools').children().add($('ul.teaserlist li:lt(2)'));}else{changeme=$('#informatives').children().children(':lt(2)');}
changeme.each(function(i){if(styleName=="narrow"){$(this).addClass("narrowed");}else{$(this).removeClass("narrowed");}});}};var isSafariOnTiger=function(){var browser=navigator.userAgent.toLowerCase();if(browser.search(/10_4/gi)>-1&&browser.search(/Safari/gi)>-1){return true;}
return false;};var areThereAds=function(){if(window.location.href.search(/blog\.zeit\.de/)>-1&&$('#wrapper #informatives').size()>0){if($('#wrapper #informatives').css("display")=='none')return false;}
return true;};return this.each(function(){if(areThereAds()&&$('#content').size()>0){var windowwidth=0;windowwidth=$(this).width();if(windowwidth==0){if($.cookie("zeitstyle")){$(this).switchStyle($.cookie("zeitstyle"));}}else if(windowwidth<options.switchwidth){switchStyle('narrow');}else{switchStyle('wide');}}});};})(jQuery);